// src/components/MapView.js

import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Set the default icon for the markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Function to generate random latitude and longitude within the boundaries of India
const getRandomCoordinates = () => {
    const latMin = 8.088;
    const latMax = 37.084;
    const lngMin = 68.077;
    const lngMax = 97.396;

    const latitude = Math.random() * (latMax - latMin) + latMin;
    const longitude = Math.random() * (lngMax - lngMin) + lngMin;

    return { latitude, longitude };
};

const MapView = () => {
    const [markers, setMarkers] = useState([]);
    const [route, setRoute] = useState([]);
    const [randomPoints, setRandomPoints] = useState([]);

    const handleMapClick = (event) => {
        const { lat, lng } = event.latlng;
        if (markers.length < 2) {
            setMarkers((prev) => [...prev, { latitude: lat, longitude: lng }]);
        }
    };

    useEffect(() => {
        // Generate 100 random points across India
        const points = Array.from({ length: 100 }, getRandomCoordinates);
        setRandomPoints(points);
    }, []);

    useEffect(() => {
        if (markers.length === 2) {
            const routeCoordinates = [
                [markers[0].latitude, markers[0].longitude],
                [markers[1].latitude, markers[1].longitude]
            ];
            setRoute(routeCoordinates);
        }
    }, [markers]);

    return (
        <MapContainer
            center={[20.5937, 78.9629]} // Center of India
            zoom={5}
            style={{ height: "500px", width: "100%" }}
            onClick={handleMapClick}
        >
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {randomPoints.map((point, index) => (
                <Marker key={index} position={[point.latitude, point.longitude]}>
                    <Popup>
                        Random Point {index + 1}: {point.latitude.toFixed(4)}, {point.longitude.toFixed(4)}
                    </Popup>
                </Marker>
            ))}
            {markers.map((marker, index) => (
                <Marker key={index} position={[marker.latitude, marker.longitude]}>
                    <Popup>
                        Marker {index + 1}: {marker.latitude.toFixed(4)}, {marker.longitude.toFixed(4)}
                    </Popup>
                </Marker>
            ))}
            {route.length === 2 && (
                <Polyline positions={route} color="blue" />
            )}
        </MapContainer>
    );
};

export default MapView;
