import React, { useState } from 'react';
import '../styles/ControlPanel.css';

const ControlPanel = ({ trafficData, onFilterChange, onOptimizeRoutes, onRefreshData, onSendAlert, onSelectStart, onSelectEnd }) => {
    const [selectedFilter, setSelectedFilter] = useState('');

    const handleFilterChange = (level) => {
        setSelectedFilter(level);
        onFilterChange(level);
    };

    return (
        <div className="control-panel">
            <h2>Control Panel</h2>
            <div className="filter-buttons">
                <button onClick={() => handleFilterChange('High')}>High</button>
                <button onClick={() => handleFilterChange('Moderate')}>Moderate</button>
                <button onClick={() => handleFilterChange('Low')}>Low</button>
            </div>
            <button onClick={onOptimizeRoutes}>Optimize Routes</button>
            <button onClick={onRefreshData}>Refresh Data</button>
            <button onClick={onSendAlert}>Send Alert</button>
            
            <h3>Select Start and End Points</h3>
            <select onChange={(e) => onSelectStart(e.target.value)}>
                <option value="">Select Start Location</option>
                {trafficData.map((location) => (
                    <option key={location.id} value={location.id}>{location.location}</option>
                ))}
            </select>
            <select onChange={(e) => onSelectEnd(e.target.value)}>
                <option value="">Select End Location</option>
                {trafficData.map((location) => (
                    <option key={location.id} value={location.id}>{location.location}</option>
                ))}
            </select>
        </div>
    );
};

export default ControlPanel;
