import React, { useState, useEffect } from 'react';
import MapView from './MapView';
import ControlPanel from './ControlPanel';
import Alerts from './Alerts';
import '../styles/TrafficDashboard.css';
import socket from '../services/WebSocketService';

function TrafficDashboard() {
    const [trafficData, setTrafficData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // WebSocket message handling
        socket.onmessage = (event) => {
            const newTrafficData = JSON.parse(event.data);
            setTrafficData(newTrafficData);
        };

        // Mock fetching data from a backend
        const mockData = [
            { location: 'Intersection A', congestionLevel: 'High', id: 1 },
            { location: 'Intersection B', congestionLevel: 'Moderate', id: 2 },
            { location: 'Intersection C', congestionLevel: 'Low', id: 3 }
        ];

        setTimeout(() => {
            setTrafficData(mockData);
            setIsLoading(false);
        }, 2000); // Simulating a 2-second loading time

        return () => {
            socket.onmessage = null; // Cleanup
        };
    }, []);

    const handleRefresh = () => {
        // Logic to refresh data
        console.log("Data refreshed!");
        // Here you could call the API or refresh the socket connection
    };

    const handleOptimizeRoutes = () => {
        // Logic to optimize routes
        console.log("Routes optimized!");
        // Add your optimization logic here
    };

    const handleSendAlert = () => {
        // Logic to send an alert
        console.log("Alert sent!");
        // Here you could implement a function to send an alert
    };

    if (isLoading) {
        return <div className="loading">Loading traffic data...</div>;
    }

    return (
        <div className="traffic-dashboard">
            <h1>Smart Traffic Management Dashboard</h1>
            <MapView trafficData={trafficData} />
            <ControlPanel 
                onRefresh={handleRefresh} 
                onOptimizeRoutes={handleOptimizeRoutes} 
                onSendAlert={handleSendAlert} 
            />
            <Alerts trafficData={trafficData} />
        </div>
    );
}

export default TrafficDashboard;
