import React, { useState } from 'react';
import './ControlPanel.css';

const ControlPanel = ({ setFilteredData, trafficData }) => {
    const [selectedLevel, setSelectedLevel] = useState('');

    const handleFilterChange = (level) => {
        setSelectedLevel(level);
        if (level) {
            const filteredData = trafficData.filter(data => data.congestionLevel.toLowerCase() === level.toLowerCase());
            setFilteredData(filteredData);
        } else {
            setFilteredData(trafficData); // Show all data if no level is selected
        }
    };

    return (
        <div className="control-panel">
            <h2>Control Panel</h2>
            <button onClick={() => handleFilterChange('high')} className={selectedLevel === 'high' ? 'active' : ''}>Filter High</button>
            <button onClick={() => handleFilterChange('moderate')} className={selectedLevel === 'moderate' ? 'active' : ''}>Filter Moderate</button>
            <button onClick={() => handleFilterChange('low')} className={selectedLevel === 'low' ? 'active' : ''}>Filter Low</button>
            <button onClick={() => handleFilterChange('')}>Show All</button>
        </div>
    );
};

export default ControlPanel;
