import React, { useState, useEffect } from 'react';
import MapView from './MapView';
import ControlPanel from './ControlPanel';
import Alerts from './Alerts';
import '../styles/TrafficDashboard.css';
import socket from '../services/WebSocketService';

function TrafficDashboard() {
    const [trafficData, setTrafficData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // Mock fetching data from a backend
        const mockData = [
            { location: 'Intersection A', congestionLevel: 'High', id: 1, latitude: 28.6129, longitude: 77.2295 },
            { location: 'Intersection B', congestionLevel: 'Moderate', id: 2, latitude: 28.6150, longitude: 77.2000 },
            { location: 'Intersection C', congestionLevel: 'Low', id: 3, latitude: 28.6100, longitude: 77.2100 },
            { location: 'Intersection D', congestionLevel: 'High', id: 4, latitude: 28.6180, longitude: 77.2200 },
            { location: 'Intersection E', congestionLevel: 'Moderate', id: 5, latitude: 28.6200, longitude: 77.2050 },
            { location: 'Intersection F', congestionLevel: 'Low', id: 6, latitude: 28.6050, longitude: 77.2150 },
            { location: 'Intersection G', congestionLevel: 'High', id: 7, latitude: 28.6000, longitude: 77.2250 },
            { location: 'Intersection H', congestionLevel: 'Moderate', id: 8, latitude: 28.6220, longitude: 77.1950 },
            { location: 'Intersection I', congestionLevel: 'Low', id: 9, latitude: 28.6240, longitude: 77.2020 },
            { location: 'Intersection J', congestionLevel: 'High', id: 10, latitude: 28.6260, longitude: 77.2100 }
        ];

        setTimeout(() => {
            setTrafficData(mockData);
            setFilteredData(mockData);
            setIsLoading(false);
        }, 2000); // Simulating a 2-second loading time
    }, []);

    if (isLoading) {
        return <div className="loading">Loading traffic data...</div>;
    }

    return (
        <div className="traffic-dashboard">
            <h1>Smart Traffic Management Dashboard</h1>
            <ControlPanel setFilteredData={setFilteredData} trafficData={trafficData} />
            <MapView trafficData={filteredData} />
            <Alerts trafficData={filteredData} />
        </div>
    );
}

export default TrafficDashboard;
