import React, { useState, useEffect } from 'react';
import MapView from './MapView';
import ControlPanel from './ControlPanel';
import Alerts from './Alerts';
import '../styles/TrafficDashboard.css';
import socket from '../services/WebSocketService';

function TrafficDashboard() {
    const [trafficData, setTrafficData] = useState([]);
    const [filteredData, setFilteredData] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        // WebSocket message handling
        socket.onmessage = (event) => {
            const newTrafficData = JSON.parse(event.data);
            setTrafficData(newTrafficData);
            setFilteredData(newTrafficData); // Initially, show all data
        };

        // Mock fetching data from a backend with lat/long data
        const mockData = [];
        for (let i = 1; i <= 100; i++) {
            mockData.push({
                location: `Intersection ${i}`,
                congestionLevel: i % 3 === 0 ? 'High' : i % 2 === 0 ? 'Moderate' : 'Low',
                latitude: 28.6139 + Math.random() * 0.05 - 0.025, // Slightly spread around the center
                longitude: 77.2090 + Math.random() * 0.05 - 0.025,
                id: i
            });
        }

        setTimeout(() => {
            setTrafficData(mockData);
            setFilteredData(mockData); // Show all data after loading
            setIsLoading(false);
        }, 2000); // Simulating a 2-second loading time

        return () => {
            socket.onmessage = null; // Cleanup
        };
    }, []);

    const handleFilterChange = (level) => {
        if (level === '') {
            setFilteredData(trafficData); // Show all data if no filter is selected
        } else {
            const filtered = trafficData.filter(data => data.congestionLevel === level);
            setFilteredData(filtered); // Show filtered data
        }
    };

    if (isLoading) {
        return <div className="loading">Loading traffic data...</div>;
    }

    return (
        <div className="traffic-dashboard">
            <h1>Smart Traffic Management Dashboard</h1>
            <MapView trafficData={filteredData} />
            <ControlPanel
                onFilterChange={handleFilterChange}
                onOptimizeRoutes={() => console.log('Optimize Routes')}
                onRefreshData={() => console.log('Refresh Data')}
                onSendAlert={() => console.log('Send Alert')}
            />
            <Alerts trafficData={filteredData} />
        </div>
    );
}

export default TrafficDashboard;
