import React from 'react';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import Typography from '@mui/material/Typography';
import '../styles/ControlPanel.css';

const ControlPanel = () => {
    // Mock functions for controlling traffic
    const handleTrafficLightChange = (color) => {
        console.log(`Traffic light changed to: ${color}`);
        // Add logic to change traffic lights in your system
    };

    const handleIncidentReport = () => {
        console.log('Incident reported!');
        // Add logic for reporting incidents
    };

    const handleResetTraffic = () => {
        console.log('Traffic reset to default state');
        // Add logic to reset traffic conditions
    };

    return (
        <div className="control-panel">
            <Typography variant="h4" gutterBottom>
                Traffic Control Panel
            </Typography>
            <Grid container spacing={2}>
                <Grid item xs={12} sm={4}>
                    <Button
                        variant="contained"
                        color="success"
                        fullWidth
                        onClick={() => handleTrafficLightChange('Green')}
                    >
                        Set Light to Green
                    </Button>
                </Grid>
                <Grid item xs={12} sm={4}>
                    <Button
                        variant="contained"
                        color="warning"
                        fullWidth
                        onClick={() => handleTrafficLightChange('Yellow')}
                    >
                        Set Light to Yellow
                    </Button>
                </Grid>
                <Grid item xs={12} sm={4}>
                    <Button
                        variant="contained"
                        color="error"
                        fullWidth
                        onClick={() => handleTrafficLightChange('Red')}
                    >
                        Set Light to Red
                    </Button>
                </Grid>
            </Grid>
            <Grid container spacing={2} style={{ marginTop: '20px' }}>
                <Grid item xs={12} sm={6}>
                    <Button
                        variant="outlined"
                        color="secondary"
                        fullWidth
                        onClick={handleIncidentReport}
                    >
                        Report Traffic Incident
                    </Button>
                </Grid>
                <Grid item xs={12} sm={6}>
                    <Button
                        variant="outlined"
                        color="default"
                        fullWidth
                        onClick={handleResetTraffic}
                    >
                        Reset Traffic Conditions
                    </Button>
                </Grid>
            </Grid>
        </div>
    );
};

export default ControlPanel;
