// MapView.js
import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet'; // Import required components
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Create a custom marker icon (pin)
const pinIcon = new L.Icon({
    iconUrl: 'https://upload.wikimedia.org/wikipedia/commons/e/ec/Marker_Icon_Blue.png', // URL to a pin icon image
    iconSize: [25, 41], // Size of the icon
    iconAnchor: [12, 41], // Point of the icon which will correspond to marker's location
    popupAnchor: [1, -34], // Point from which the popup should open relative to the iconAnchor
});

function MapView({ trafficData }) {
    // Center the map based on average lat/lng if necessary
    const center = trafficData.length > 0
        ? [trafficData[0].latitude, trafficData[0].longitude]
        : [0, 0]; // Default center if no data

    return (
        <MapContainer center={center} zoom={12} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {trafficData.map((data, index) => (
                <Marker key={index} position={[data.latitude, data.longitude]} icon={pinIcon}>
                    <Popup>
                        <div>
                            <strong>Location:</strong> {data.location} <br />
                            <strong>Vehicle Count:</strong> {data.vehicleCount} <br />
                            <strong>Average Speed:</strong> {data.avg_speed} km/h <br />
                            <strong>Congestion Level:</strong> {data.congestionLevel} <br />
                            <strong>Weather Condition:</strong> {data.weather_condition} <br />
                            <strong>Incident:</strong> {data.incident}
                        </div>
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
}

export default MapView;
