import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Default icon for markers
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

// Function to generate random coordinates near a given point
const getRandomCoordinates = (center, range) => {
    const [lat, lng] = center;
    const randomLat = lat + (Math.random() - 0.5) * range; // Randomize latitude
    const randomLng = lng + (Math.random() - 0.5) * range; // Randomize longitude
    return [randomLat, randomLng];
};

const MapView = ({ trafficData }) => {
    const delhiCoordinates = [28.6139, 77.2090]; // Coordinates for Delhi
    const markers = Array.from({ length: 10 }, (_, index) => {
        const congestionLevels = ['High', 'Moderate', 'Low'];
        return {
            id: index + 1,
            location: `Location ${index + 1}`,
            congestionLevel: congestionLevels[Math.floor(Math.random() * congestionLevels.length)],
            position: getRandomCoordinates(delhiCoordinates, 0.05), // Randomize position within a range of 0.05 degrees
        };
    });

    return (
        <MapContainer center={delhiCoordinates} zoom={13} style={{ height: "500px", width: "100%" }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {markers.map((data) => (
                <Marker 
                    key={data.id} 
                    position={data.position}
                >
                    <Popup>
                        <strong>{data.location}</strong><br />
                        Congestion Level: {data.congestionLevel}
                    </Popup>
                </Marker>
            ))}
        </MapContainer>
    );
};

export default MapView;
