import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import { MarkerClusterGroup } from 'react-leaflet-markercluster';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet.markercluster/dist/MarkerCluster.css';
import 'leaflet.markercluster/dist/MarkerCluster.Default.css';

const getIconColor = (level) => {
    const percentage = level * 100;
    if (percentage > 70) return 'red';
    if (percentage > 30) return 'yellow';
    return 'green';
};

const MapView = ({ trafficData }) => {
    return (
        <MapContainer center={[27.5361, -48.1736]} zoom={12} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />
            <MarkerClusterGroup>
                {trafficData.map((data) => {
                    const { congestion_level, latitude, longitude } = data;

                    if (typeof latitude !== 'number' || typeof longitude !== 'number') {
                        console.error('Invalid coordinates:', latitude, longitude);
                        return null;
                    }

                    const color = getIconColor(congestion_level);
                    const icon = L.divIcon({
                        className: 'custom-icon',
                        html: `<div style="background-color: ${color}; width: 20px; height: 20px; border-radius: 50%;"></div>`,
                        iconSize: [20, 20],
                    });

                    return (
                        <Marker key={data.sensor_id} position={[latitude, longitude]} icon={icon}>
                            <Popup>
                                <span>Weather Condition: {data.weather_condition}</span>
                                <br />
                                <span>Incident: {data.incident}</span>
                            </Popup>
                        </Marker>
                    );
                })}
            </MarkerClusterGroup>
        </MapContainer>
    );
};

export default MapView;
