const MapView = ({ trafficData }) => {
    return (
        <MapContainer center={[27.5361, -48.1736]} zoom={12} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />
            {trafficData.map((data, index) => {
                const { congestion_level, latitude, longitude } = data;

                // Validate latitude and longitude
                if (typeof latitude !== 'number' || typeof longitude !== 'number') {
                    console.error('Invalid coordinates:', latitude, longitude);
                    return null; // Skip this entry if coordinates are invalid
                }

                const color = getIconColor(congestion_level);
                const icon = L.divIcon({
                    className: 'custom-icon',
                    html: `<div style="background-color: ${color}; width: 20px; height: 20px; border-radius: 50%;"></div>`,
                    iconSize: [20, 20],
                });

                return (
                    <Marker key={index} position={[latitude, longitude]} icon={icon}>
                        <Popup>
                            <span>Congestion Level: {(congestion_level * 100).toFixed(1)}%</span>
                            <br />
                            <span>Location: {data.location}</span>
                            <br />
                            <span>Vehicle Count: {data.vehicle_count}</span>
                            <br />
                            <span>Average Speed: {data.avg_speed.toFixed(1)} km/h</span>
                            <br />
                            <span>Signal Status: {data.signal_status}</span>
                            <br />
                            <span>Weather Condition: {data.weather_condition}</span>
                            <br />
                            <span>Incident: {data.incident}</span>
                        </Popup>
                    </Marker>
                );
            })}
        </MapContainer>
    );
};
