import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';

// Function to determine the icon color based on congestion level (0.0 to 1.0)
const getIconColor = (level) => {
    const percentage = level * 100; // Convert to percentage
    if (percentage > 70) return 'red'; // High congestion
    if (percentage > 30) return 'yellow'; // Medium congestion
    return 'green'; // Low congestion
};

const MapView = ({ trafficData }) => {
    return (
        <MapContainer center={[28.6139, 77.2090]} zoom={12} style={{ height: '500px', width: '100%' }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution="&copy; <a href='https://www.openstreetmap.org/copyright'>OpenStreetMap</a> contributors"
            />
            {trafficData.map((data, index) => {
                const { predicted_congestion_level, latitude, longitude } = data; // Adjust according to your data structure
                const color = getIconColor(predicted_congestion_level); // Use the modified function
                const icon = L.divIcon({
                    className: 'custom-icon',
                    html: `<div style="background-color: ${color}; width: 20px; height: 20px; border-radius: 50%;"></div>`,
                    iconSize: [20, 20],
                });

                return (
                    <Marker key={index} position={[latitude, longitude]} icon={icon}>
                        <Popup>
                            <span>Congestion Level: {(predicted_congestion_level * 100).toFixed(1)}%</span>
                        </Popup>
                    </Marker>
                );
            })}
        </MapContainer>
    );
};

export default MapView;
