import React, { useEffect, useRef } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-routing-machine';
import L from 'leaflet';

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
    iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
    iconUrl: require('leaflet/dist/images/marker-icon.png'),
    shadowUrl: require('leaflet/dist/images/marker-shadow.png'),
});

const MapView = ({ trafficData, startCoordinates, endCoordinates }) => {
    const routingControlRef = useRef(null);

    const RoutingControl = ({ start, end }) => {
        const map = useMap();

        useEffect(() => {
            if (!start || !end) return;

            // Remove existing routing control if present to avoid duplicates
            if (routingControlRef.current) {
                map.removeControl(routingControlRef.current);
            }

            // Create a new routing control and save a reference
            routingControlRef.current = L.Routing.control({
                waypoints: [
                    L.latLng(start.latitude, start.longitude),
                    L.latLng(end.latitude, end.longitude)
                ],
                routeWhileDragging: true
            }).addTo(map);

            // Filter traffic data along the route (simplified logic)
            const routeBounds = L.latLngBounds(
                [start.latitude, start.longitude],
                [end.latitude, end.longitude]
            );

            trafficData.forEach((data) => {
                const point = L.latLng(data.latitude, data.longitude);
                if (routeBounds.contains(point)) {
                    L.marker([data.latitude, data.longitude]).addTo(map);
                }
            });

            return () => {
                if (routingControlRef.current) {
                    map.removeControl(routingControlRef.current);
                    routingControlRef.current = null;
                }
            };
        }, [start, end, map]);

        return null;
    };

    return (
        <MapContainer center={[28.6139, 77.2090]} zoom={11} style={{ height: "500px", width: "100%" }}>
            <TileLayer
                url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                attribution='&copy; <a href="http://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
            />
            {trafficData.map((data) => (
                <Marker key={data.id} position={[data.latitude, data.longitude]}>
                    <Popup>
                        <strong>{data.location}</strong><br />
                        Congestion Level: {data.congestionLevel}
                    </Popup>
                </Marker>
            ))}
            {startCoordinates && endCoordinates && (
                <RoutingControl start={startCoordinates} end={endCoordinates} />
            )}
        </MapContainer>
    );
};

export default MapView;
